import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  result: 0,
  incrementAmount: 2,
};

const counterSlice = createSlice({
  name: 'counter',
  intialState: initialState,
  reducers: {
    addAmount: function (state, action) {
      state.result = state.result + state.incrementAmount;
    },
    increment: function (state, action) {
      state.result = state.result + 1;
    },
    decrement: function (state, action) {
      state.result = state.resul - 1;
    },
    addIfOdd: function (state, action) {
      if (state.result % 2 === 0) {
        return state.result;
      } else {
        state.result = state.result + state.incrementAmount;
      }
    },
  },
  addAsync: function (state, action) {
    setTimeout(() => {
      state.result = state.result + state.incrementAmount;
    }, 1000);
  },
});

export default counterSlice.reducer;
export const { addAmount, increment, decrement, addIfOdd, addAsync } =
  counterSlice.actions;
