import React from 'react';
import styles from './Counter.module.css';
import { useSelector, useDispatch } from 'react-redux';
import {
  increment,
  decrement,
  addAmount,
  addIfOdd,
  addAsync,
  formInput,
} from '../redux/feature/counter.feature';

export function Counter() {
  //get data from redux store

  const count = useSelector((state) => state.counter.result);

  const dispatch = useDispatch();

  //addincrement function
  const counterIncrement = () => {
    dispatch(increment());
  };
  //decrement function
  const counterDecrement = () => {
    dispatch(decrement());
  };

  const counterAddAmount = () => {
    dispatch(addAmount());
  };

  //addIfOdd function
  const counterAddIfOdd = () => {
    dispatch(addIfOdd());
  };
  //addAsync function
  const counterAddAsync = () => {
    dispatch(addAsync());
  };
  //form input function
  const counterFormInput = (e) => {
    e.preventDefault();
    const value = e.target.value;
    dispatch(formInput(value));
  };
