import React, { useState } from 'react';

import styles from './Counter.module.css';

export function Counter() {
  const [incrementAmount, setIncrementAmount] = useState(2);
  const [result, setresult] = useState(0);
  //addAmount function
  const addAmount = () => {
    setresult(result + incrementAmount);
  };
  //addincrement function
  const increment = () => {
    setresult(result + 1);
  };
  //decrement function
  const decrement = () => {
    if (result <= 0) {
      setresult(0);
    } else {
      setresult(result - 1);
    }
  };
  //addIfOdd function
  const addIfOdd = () => {
    if (result % 2 === 0) {
      setresult(result);
    } else {
      setresult(result + incrementAmount);
    }
  };
  //addAsync function
  const addAsync = () => {
    setTimeout(() => {
      setresult(result + incrementAmount);
    }, 1000);
  };
  return (
    <div>
      <div className={styles.row}>
        <button onClick={decrement} className={styles.button}>
          -
        </button>
        <span className={styles.value}>{result}</span>
        <button onClick={increment} className={styles.button}>
          +
        </button>
      </div>
      <div className={styles.row}>
        <input
          className={styles.textbox}
          type="number"
          value={incrementAmount}
          onChange={(e) => setIncrementAmount(e.target.value)}
        />
        <button onClick={addAmount} className={styles.button}>
          Add Amount
        </button>
        <button onClick={addAsync} className={styles.asyncButton}>
          Add Async
        </button>
        <button onClick={addIfOdd} className={styles.button}>
          Add If Odd
        </button>
      </div>
    </div>
  );
}
