import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
  increment,
  decrement,
  incrementBy,
} from '../redux/feature/counter.feature';

const CounterRedux = () => {
  //fetching the counter state
  const counterState = useSelector((state) => {
    return state.counter;
  });
  //destructuing counterState
  const { counter } = counterState;

  //dispatching the action
  const dispatch = useDispatch();

  const increment = () => {
    dispatch(increment);
  };
  const decrement = () => {
    dispatch(decrement());
  };
  const incrementBy = () => {
    dispatch(incrementBy(5));
  };

  return (
    <React.Fragment>
      <div className="container">
        <div className="row mt-5 text-center">
          <div className="col">
            <div className="card">
              <div className="card-body">
                <p className="h1">Count {counter}</p>
                <button
                  onClick={increment}
                  type="button"
                  className="btn btn-primary me-3"
                >
                  Increment
                </button>
                <button
                  onClick={decrement}
                  type="button"
                  className="btn btn-warning me-3"
                >
                  Decrement
                </button>
                <button
                  onClick={incrementBy}
                  type="button"
                  className="btn btn-success"
                >
                  Increment By 5
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </React.Fragment>
  );
};

export default CounterRedux;
